import React, { useEffect } from "react";
import { useState } from "react";
import bootstrap from "bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "../stylesheets/PokemonCard.css";

export const PokemonCard = (props) => {
  const [pokemon, setPokemon] = useState([]);

  useEffect(() => {
    fetch(props.url)
      .then((response) => response.json())
      .then((poke) => {
        setPokemon(poke);
      });
  }, []);
  return (
    pokemon && (
      <div className={pokemon.types && pokemon.types[0].type.name + " card"}>
        <div className="container-img-number">
          <h2>#{pokemon.id}</h2>
          <img
            src={pokemon.sprites && pokemon.sprites.front_default}
            className="card-img-top img-pokemon"
            alt="img-pokemon"
          />
        </div>
        <div className="card-body">
          <h5 className="card-title">
            {pokemon.name && pokemon.name.toUpperCase()}
          </h5>
          <p className="card-text">
            Some quick example text to build on the card title and make up the
            bulk of the card's content.
          </p>
        </div>
        <ul className="">
          {pokemon.types &&
            pokemon.types.map((type) => (
              <li className="list-group-item">{type.type.name}</li>
            ))}
        </ul>
      </div>
    )
  );
};
