import React, { useEffect } from "react";
import { useState } from "react";
import { AiFillPropertySafety } from "react-icons/ai";
import { PokemonCard } from "./PokemonCard";
import "../stylesheets/PokemonList.css";
/* export const PokemonList = () => {
  const [pokemonList, setPokemonList] = useState([]);
  useEffect(() => {
    fetch("https://pokeapi.co/api/v2/pokemon?limit=10&offset=0")
      .then((response) => response.json())
      .then(({ results }) => {
        setPokemonList(results);
      });
  }, []); */

export const PokemonList = () => {
  const [pokemon, setPokemon] = useState([]);
  const [pokemonList, setPokemonList] = useState([]);
  /*useEffect(() => {
    async function fetchData() {
      const response = await fetch(
        "https://pokeapi.co/api/v2/pokemon?limit=10&offset=0"
      );
      const newPokemonList = await response.json();
      setPokemonList(newPokemonList);
      Promise.all(pokemonList.map((poke) => fetchPokemon(poke.url)));
    }
    async function fetchPokemon(url) {
      const pokemonFetch = await fetch(url);
      const pokemonJson = await pokemonFetch.json();
      setPokemon(pokemonJson);
    }
    fetchData();
  }, []);*/
  useEffect(() => {
    async function fetchData() {
      const response = await fetch(
        "https://pokeapi.co/api/v2/pokemon?limit=10&offset=0"
      );
      const data = await response.json();
      const newPokemonList = data.results;
      setPokemonList(newPokemonList);

      const pokemonData = await Promise.all(
        newPokemonList.map((pokemon) => fetchPokemon(pokemon.url))
      );
      setPokemon(pokemonData);
    }

    async function fetchPokemon(url) {
      const response = await fetch(url);
      const data = await response.json();
      return data;
    }

    fetchData();
  }, []);
  return (
    <div className="contenedor-lista">
      {pokemon.map((pokemon1) => (
        <PokemonCard
          name={pokemon1.name}
          url={pokemon1.url}
          key={pokemon1.name}
          sprites={pokemon1.sprites}
        />
      ))}
    </div>
  );
};
