import React, { useEffect } from "react";
import { useState } from "react";
import { useLocation } from "react-router-dom";
async function fetchDetails(info) {
  const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${info}`);
  const data = await response.json();
  return {
    details: {
      name: data.name,
      height: data.height,
      weight: data.weight,
    },
    abilities: data.abilities,
  };
}

async function fetchAbilities(info) {
  const abilities = await fetchDetails(info).abilities;
  const abilitiesJson = abilities.json();
  return abilitiesJson;
}
export const PokemonDetails = (props) => {
  const location = useLocation();
  const infoName = location.state.name;
  const allAbilities = fetchAbilities(infoName);
  console.log(allAbilities);
  const [pokemonDetails, setPokemonDetails] = useState([]);
  useEffect(() => {
    fetchDetails(infoName).then((detailsData) =>
      setPokemonDetails(detailsData)
    );
  }, []);

  return (
    <div className="main-container">
      {allAbilities.map((details) => (
        <li>details.abtily.name</li>
      ))}
    </div>
  );
};
